import tachyons from "tachyons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faQuoteLeft } from "@fortawesome/free-solid-svg-icons";
import "../../index.css";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";




export const CardInfo = ({
  quote = "lorem sample text that is what this is you already know it boi",
  author = "lorem fait",
  index = 'unknown',


}) => {
  const [expand, setExpand] = useState(false);
  const [expand2, setExpand2] = useState(null);
  // I did not export bc of getter vs setter error when using logic for changing array colors in dark mode
  let colors = [
    "#d61a1a",
    "#21d214",
    "#FF6633",
    "#0ed6d3",
    "#ba9a2f",
    "#3369ff",
    "#60514c",
    "#32724f",
    "#c91ead",
    "#d82b6b",
    "#3029fb",
    "#657077",
    "#99de3f",
    "#b433ff",
    "#590e0e",
    "#131142",
  ];
  let colors2 = [
    "#d61a1a",
    "#21d214",
    "#FF6633",
    "#0ed6d3",
    "#ba9a2f",
    "#3369ff",
    "#c9a89d",
    "#4eba7f",
    "#c91ead",
    "#d82b6b",
    "#9894f7",
    "#98aab5",
    "#99de3f",
    "#9d48cf",
    "#ff94b0",
    "#FFF",
  ];
  //////////////////////////////////////////////////

  // useSelector
  const lightMode = useSelector((state) => state.light);

  // base set up for light mode vs dark mode toggle
  lightMode ? (colors = colors) : (colors = colors2);
  const newColorState = useSelector((state) => state.color);


// * 
  // toggle expand handler
  const handleExpand = () => {
    // setExpand(!expand);
    setExpand2(quote);
    console.log(expand2)
  };

  useEffect(() => {
handleExpand()
  }, [])
  //////////////////////////////////////////////////

  return (
    <>
      {expand ? (
        <div
          onClick={handleExpand}
          style={{
            color: colors[newColorState],
            border: `solid 2px ${colors[newColorState]}`,
            width: '45rem',
            height: '30rem'

          }}
          className="quote-info-box tc mt4"
          id="quote-info-box"
        >
          <h5
            id="text"
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
            className="pa3 pt2"
          >
            <FontAwesomeIcon
              style={{
                color: colors[newColorState],
                transition: "all .5s ease-in-out",
              }}
              icon={faQuoteLeft}
            />
            {quote}
          </h5>
          <p
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
          >
            - {author}
          </p>
          <p
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
          >
            {/* Quote #{index} */}
          </p>
        </div>
      ) : (
        <div
          onClick={handleExpand}
          style={{
            color: colors[newColorState],
            border: `solid 2px ${colors[newColorState]}`,
          }}
          className="quote-info-box tc mt4"
          id="quote-info-box"
        >
          <h5
            id="text"
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
            className="pa3 pt2"
          >
            <FontAwesomeIcon
              style={{
                color: colors[newColorState],
                transition: "all .5s ease-in-out",
              }}
              icon={faQuoteLeft}
            />
            {quote}
          </h5>
          <p
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
          >
            - {author}
          </p>
          <p
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
          >
            {/* Quote #{index} */}
          </p>
        </div>
      )}
    </>
  );
};

