import tachyons from "tachyons";
import { CardInfo } from "./CardInfo";
import "../../index.css";
import { colors } from "../MainPage/Buttons";
import { useSelector } from "react-redux";
import { useState } from "react";

export const SearchQuotes = () => {
  const newColorState = useSelector((state) => state.color);
  const [search, setSearch] = useState('');
  const searchQuotes = (searchValue) => {
    setSearch
  }

  return (
    <div className="search-container tc">
      <input
        style={{
          color: colors[newColorState],
          border: `solid 4px ${colors[newColorState]}`,
        }}
        onChange={(e) => searchQuotes(e.target.value)}
        className="tc f6 dim ba bw2 ph3 pv2 mb2 dib"
        type="search"
        placeholder="search for quotes..."
      />
      <div className="card-containers">
        <CardInfo quote="this is why im hot" author="Mims" />
        <CardInfo quote="weezy f baby, pls say the baby" author="lil wayne" />
        <CardInfo quote="to be or not to be" author="shaeksbere guy" />
        <CardInfo quote="aint no holla back girl" author="gwen stefani" />
        <CardInfo quote="you the best I ever had" author="Drake" />
        <CardInfo
          quote="Im testing the search functionality"
          author="Cassius"
        />
        <CardInfo
          quote="yo exuse me miss, i saw you from across the room"
          author="chris brown"
        />
        <CardInfo quote="this is why im hot" author="Mims" />
        <CardInfo quote="this is why im hot" author="Mims" />
        <CardInfo quote="this is why im hot" author="Mims" />
        <CardInfo quote="this is why im hot" author="Mims" />
      </div>
    </div>
  );
};

