import tachyons from "tachyons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faQuoteLeft } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux/es/hooks/useSelector";
import './transition.css';
import styled, {keyframes} from 'styled-components';


// exports


const Text = () => {
  const [quoteArr, setquoteArr] = useState([]);
  const [fade, setFade] = useState(false)
  setTimeout(() => {
    setFade({

    })
  }, 1000);


  const fetchQuotes = async () => {
    try {
      const data = await axios.get("https://type.fit/api/quotes");
      setquoteArr(data.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchQuotes();
  }, []);

const colors = [
  "#FF6633",
  "#21d214",
  "#d61a1a",
  "#0ed6d3",
  "#ba9a2f",
  "#3369ff",
  "#60514c",
  "#32724f",
  "#c91ead",
  "#d82b6b",
  "#3029fb",
  "#657077",
  "#99de3f",
  "#b433ff",
  "#590e0e",
  "#131142",
];

  const newQuoteState = useSelector((state) => state.quote);
  const newColorState = useSelector((state) => state.color);
  const newFadeState = useSelector((state) => state.fade);


  return (
    <>
      {" "}
      <div className="quote-box">

        <h2
          style={{
            color: colors[newColorState],
            transition: "all .5s ease-in-out",
          }}
        >
          <FontAwesomeIcon icon={faQuoteLeft} />
          {quoteArr[3]
            ? " " + quoteArr[newQuoteState].text
            : console.log("array is empty")}
        </h2>
        <p
          style={{
            color: colors[newColorState],
            transition: "all .5s ease-in-out",
          }}
        >
          -{" "}
          {quoteArr[3]
            ? quoteArr[newQuoteState].author
              ? quoteArr[newQuoteState].author
              : "anonymous"
            : console.log("array is empty")}
        </p>

      </div>
    </>
  );
};

export default Text;


