import tachyons from "tachyons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faQuoteLeft } from "@fortawesome/free-solid-svg-icons";
import "../../index.css";
import { colors } from "../MainPage/Buttons";
import { useSelector } from "react-redux";

export const CardInfo = ({
  quote = "lorem sample text that is what this is you already know it boi",
  author = "lorem fait",
}) => {
  const newColorState = useSelector((state) => state.color);

  return (
    <div
      style={{ border: newColorState }}
      className="quote-info-box tc mt4"
      id="quote-info-box"
    >
      <h5
        id="text"
        style={{
          color: { newColorState },
          transition: "all .5s ease-in-out",
        }}
        className="pa3 pt2"
      >
        <FontAwesomeIcon icon={faQuoteLeft} />
        {quote}
      </h5>
      <p
        style={{
          color: { newColorState },
          transition: "all .5s ease-in-out",
        }}
      >
        - {author}
      </p>
    </div>
  );
};

