import React from "react";
import Card from "./components/MainPage/Card";
import tachyons from "tachyons";
import { useDispatch, useSelector } from "react-redux";
import "./App.css";
import { colors } from "./components/MainPage/Buttons";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSun } from "@fortawesome/free-solid-svg-icons";
import { goLightMode, goDarkMode } from "./Redux/Action";

function App() {
  const dispatch = useDispatch();
  const newColorState = useSelector((state) => state.color);
  const lightMode = useSelector((state) => state.light);

  // eslint-disable-next-line no-lone-blocks
  {
    lightMode
      ? (document.body.style.backgroundColor = "white")
      : (document.body.style.backgroundColor = "rgb(32, 32, 32)");
  }
   {
     lightMode
       ? (colors)
       : (colors = [
        'red'
       ]);
   }

  return (
    <div className="tc">
      <div className="dark-light tc mb4">
        {lightMode ? (
          <FontAwesomeIcon
            className=""
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
            icon={faMoon}
            size="2x"
            onClick={() => {
              dispatch(goDarkMode());
            }}
          />
        ) : (
          <FontAwesomeIcon
            className=""
            style={{
              color: colors[newColorState],
              transition: "all .5s ease-in-out",
            }}
            icon={faSun}
            size="2x"
            onClick={() => {
              dispatch(goLightMode());
            }}
          />
        )}
      </div>
      <Link to="/search">
        <a
          target="_blank"
          id="new-quote"
          style={{
            color: colors[newColorState],
            transition: "all .5s ease-in-out",
          }}
          className="f6 link dim ba bw2 ph3 pv2 mb3 dib"
          href="#0"
        >
          Search For Quotes
        </a>
      </Link>
      <div
        style={{
          border: `2px solid ${colors[newColorState]}`,
          transition: "all .5s ease-in-out",
        }}
        className="App tc"
      >
        <Card />
      </div>

      <footer
        style={{
          color: `${colors[newColorState]}`,
          transition: "all .5s ease-in-out",
        }}
        className="tc ma5"
      >
        Developed By{" "}
        <a
          style={{
            color: `${colors[newColorState]}`,
            transition: "all .5s ease-in-out",
          }}
          href="https://github.com/cassius2828"
        >
          Cassius Reynolds{" "}
        </a>
      </footer>
    </div>
  );
}

export default App;
