import { combineReducers } from "redux";
import { NEW_COLOR, NEW_QUOTE, NEXT_QUOTE, PREV_QUOTE } from "./Action";

  const newQuoteState = useSelector((state) => state.quote);

const colorReducer = (state = 0, action) => {
  switch (action.type) {
    case NEW_COLOR:
      return Math.floor(Math.random() * 16);
    default:
      return state;
  }
};

// const quoteState = colors[0]

const quoteReducer = (state = 0, action) => {
  switch (action.type) {
    case NEW_QUOTE:
      return Math.floor(Math.random() * 1643);
    default:
      return state;
  }
};

export const prevReducer = (state = newQuoteState, action) => {
  switch (action.type) {
    case PREV_QUOTE:
      return newQuoteState - 1;

    default:
      return state;
  }
};
export const nextReducer = (state = newQuoteState, action) => {
  switch (action.type) {
    case NEXT_QUOTE:
      return newQuoteState + 1;
    default:
      return state;
  }
};

export const rootReducer = combineReducers({
  color: colorReducer,
  quote: quoteReducer,
  prev: prevReducer,
  next: nextReducer
});
