import tachyons from "tachyons";
import { CardInfo } from "./CardInfo";
import ErrorBoundry from "../../ErrorBoundary";
import "../../index.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
// import { data } from "./TestAPI";
import { shorterQuotes, categories } from "../../filteredQuotes";
import InfiniteScroll from "react-infinite-scroll-component";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowAltCircleUp,
  faMoon,
  faSun,
  faHome,
} from "@fortawesome/free-solid-svg-icons";
import { goDarkMode, goLightMode, grabCard } from "../../Redux/Action";
import { Link } from "react-router-dom";

//////////////////////////////////////////////////
//////////////////////////////////////////////////
//////////////////////////////////////////////////

export const SearchQuotes = () => {

  const [category, setCategory] = useState('');
  // I did not export bc of getter vs setter error when using logic for changing array colors in dark mode
  let colors = [
    "#d61a1a",
    "#21d214",
    "#FF6633",
    "#0ed6d3",
    "#ba9a2f",
    "#3369ff",
    "#60514c",
    "#32724f",
    "#c91ead",
    "#d82b6b",
    "#3029fb",
    "#657077",
    "#99de3f",
    "#b433ff",
    "#590e0e",
    "#131142",
  ];
  let colors2 = [
    "#d61a1a",
    "#21d214",
    "#FF6633",
    "#0ed6d3",
    "#ba9a2f",
    "#3369ff",
    "#c9a89d",
    "#4eba7f",
    "#c91ead",
    "#d82b6b",
    "#9894f7",
    "#98aab5",
    "#99de3f",
    "#9d48cf",
    "#ff94b0",
    "#FFF",
  ];

  //////////////////////////////////////////////////
  // base set up for light mode vs dark mode toggle
  const lightMode = useSelector((state) => state.light);

  lightMode
    ? (document.body.style.backgroundColor = "white")
    : (document.body.style.backgroundColor = "rgb(32, 32, 32)");

  lightMode ? (colors = colors) : (colors = colors2);

  // base set up for light mode vs dark mode toggle
  //////////////////////////////////////////////////

  const dispatch = useDispatch();

  const newColorState = useSelector((state) => state.color);
  const [search, setSearch] = useState("");
  const [filteredResults, setFilteredResults] = useState([]);

  // search functionality
  const searchQuotes = (searchValue) => {
    setSearch(searchValue);
    if (search !== "") {
      const filteredData = shorterQuotes.filter((item) => {
        return Object.values(item)
          .join(" ")
          .toLocaleLowerCase()
          .includes(search.toLocaleLowerCase());
      });
      setFilteredResults(filteredData);
    } else {
      setFilteredResults(shorterQuotes);
    }
  };

  const grabCard1 = (selector) => {
    const theCard = document.getElementById(selector);

    console.log(theCard);
    console.log("hi");


  };

   const handleCategoryChange = () => {
     const select = document.getElementById("dropdown-filter").value;
     setCategory(select);
   };

   useEffect(() => {
     handleCategoryChange();
     console.log(category)
   }, [category]);

  //  this does not make sense
    let updatedList = filteredResults.filter((item) => {
     let a = item.tags.map((tag, index) => {
        return tag[index]
      });
      return a === category

  });

  // ! START OF RETURN

  return (
    <div className="search-container tc">
      <>
        <div className="search-icon-container mb4">
          <Link to="/">
            <FontAwesomeIcon
              icon={faHome}
              size="2x"
              style={{
                color: colors[newColorState],
                transition: "all .5s ease-in-out",
              }}
            />
          </Link>

          {lightMode ? (
            <FontAwesomeIcon
              className=""
              style={{
                color: colors[newColorState],
                transition: "all .5s ease-in-out",
                cursor: "pointer",
              }}
              icon={faMoon}
              size="2x"
              onClick={() => {
                dispatch(goDarkMode());
              }}
            />
          ) : (
            <FontAwesomeIcon
              className=""
              style={{
                color: colors[newColorState],
                cursor: "pointer",
                transition: "all .5s ease-in-out",
              }}
              icon={faSun}
              size="2x"
              onClick={() => {
                dispatch(goLightMode());
              }}
            />
          )}

          <a href="#back-to-top">
            <FontAwesomeIcon
              className="back-to-top"
              icon={faArrowAltCircleUp}
              size="2x"
              style={{
                color: colors[newColorState],
                transition: "all .5s ease-in-out",
              }}
            />
          </a>
        </div>

        <div className="filter-container">
          <input
            style={{
              color: colors[newColorState],
              border: `solid 4px ${colors[newColorState]}`,
              backgroundColor: `transparent`,
            }}
            onChange={(e) => searchQuotes(e.target.value)}
            className="tc f6 dim ba bw2 ph3 pv2 mb2 dib"
            type="search"
            placeholder="search for quotes..."
          />
          <select onChange={handleCategoryChange} id="dropdown-filter">
            <option key="blank"></option>
            {categories.map((item) => {
              return <option key={item}>{item}</option>;
            })}
          </select>
        </div>

        <ErrorBoundry>
          <InfiniteScroll
            className="infinite-scroll mb4"
            dataLength={shorterQuotes.length}
            // next={shorterQuotes}
            hasMore={true} // Replace with a condition based on your data source
            height={620}
          >
            <div id="back-to-top"></div>
            <div className="card-containers">
              {category !== ""
                ? 
                <h1>{cat}</h1>
